package iscteiul.ista.battleship;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;

public class BargTest1 {

    @Test
    public void testBargeCreationAndPosition() {
        Position origin = new Position(4, 5);
        Barge b = new Barge(Compass.NORTH, origin);

        assertEquals(Integer.valueOf(1), b.getSize());
        assertNotNull(b.getPositions());
        assertEquals(1, b.getPositions().size());

        Position p = (Position) b.getPositions().get(0);
        assertEquals(4, p.getRow());
        assertEquals(5, p.getColumn());
    }

    @Test
    public void testOccupiesAndShootBehavior() {
        Position origin = new Position(0, 0);
        Barge b = new Barge(Compass.EAST, origin);

        Position pos = new Position(0, 0);
        assertTrue(b.occupies(pos));
        assertTrue(b.stillFloating());

        b.shoot(pos);
        assertFalse(b.stillFloating());
    }
}
